<?phpclass cjTask extends sfBaseTask{  protected function configure()  {    // add your own arguments here     $this->addArguments(array(       new sfCommandArgument('download', sfCommandArgument::OPTIONAL, 'Skip downloading input file if false.'),     ));    $this->addOptions(array(      new sfCommandOption('application', null, sfCommandOption::PARAMETER_REQUIRED, 'The application name', 'recording'),      new sfCommandOption('env', null, sfCommandOption::PARAMETER_REQUIRED, 'The environment', 'dev'),      new sfCommandOption('connection', null, sfCommandOption::PARAMETER_REQUIRED, 'The connection name', 'propel'),      // add your own options here    ));    $this->namespace        = 'recording';    $this->name             = 'cj';    $this->briefDescription = 'Imports Commission Junction Data Dumps';    $this->detailedDescription = <<<EOFImports Commission Junction Data Dumps  [php symfony sitemap|INFO]EOF;  }  protected function execute($arguments = array(), $options = array())  {    // initialize the database connection    $databaseManager = new sfDatabaseManager($this->configuration);    $connection = $databaseManager->getDatabase($options['connection'] ? $options['connection'] : null)->getConnection();    $cj = new CJ();    if($arguments['download'] == 'false') {        $download = false;    } else {        $download = true;    }    $cj->importMusiciansFriendData($download);  }}